import React,{useContext, useEffect} from 'react'
import noteContext from '../context/notes/NotesContext'
const About = () => {
  const a=useContext(noteContext)
  useEffect(()=>{
    a.update()
  },[])
  return (
    <div>
      this is about and {a.name} which is iin class {a.class }
    </div>
  )
}

export default About
